              difficulty: { basic: "BASIS", core: "KERN", advanced: "FORTGESCHRITTEN", elite: "ELITE" },
              objective_title: "Aktuelles Missionsziel",
              monitor_title: "Thermodynamik-Monitor",
              footer_left: "GP2.02_THERMODYNAMIK_I // KNOTEN: BASEL",
